    for (i = 0; i < 5; i++) 
        printf("%d ", arr2[i]); 
    printf("\n"); 

    // Finding common elements 
    printf("Common elements: "); 
    for (i = 0; i < 5; i++) { 
        for (j = 0; j < 5; j++) { 
            if (arr1[i] == arr2[j]) 
                printf("%d ", arr1[i]); 
        } 
    } 

    return 0; 
} 

