function isPrime(num) {
  // The first condition checks whether the number is even or not. If it is even, then we know that it can't be a prime number since primes are odd numbers.
  if (num % 2 === 0) {
    return false;
  }

  // Now we check if the number is divisible by any number less than itself. If so, then it isn't a prime number because every number greater than one has at least two factors, which means it is divisible by more than just itself. So if your number passes this test, then it must be a prime number!
  else {
    for (let j = 3; j <= Math.floor(Math.sqrt(num)); j += 2) {
      if (num % j === 0) {
        return false;
      }
    }
  }
  return true;
}

